con:config.script:
// To get the output value, use the built-in result map object
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def projectPath = groovyUtils.projectPath
def directoryName = projectPath + "/testData"
log.info ("Directory: " + directoryName)
def row = testRunner.testCase.testSteps["DataSource"].currentRow
def allFiles = []
new File( directoryName ).eachFile() { file ->
    if( file.name =~ /.txt/ ) {
        allFiles.add( file.name )	}
}
if ( (row + 1) <= allFiles.size ) {
    // Output to the test step property called inputData
    result["inputData"] = new File( directoryName + "/" + allFiles[row] ).text
}

con:config.con:executeScript:
def testCasePropertyName = testRunner.testCase.getPropertyValue("name")
def testCasePropertyDescription = testRunner.testCase.getPropertyValue("description")

testRunner.testCase.setPropertyValue( "name", "2" )
testRunner.testCase.setPropertyValue( "description", "qwerty )
